import numpy as np
import matplotlib.pyplot as plt


def analog_signal(t): # 12
    """returns function for base signal."""
    fis = [1, 15, 18, 22, 60]
    s = 0
    for fi in fis:
        s += np.cos(2 * np.pi * t * fi)
    return s

"""Построим график исходного сигнала"""

timestamps = np.linspace(0, 30, 500)
signal_values = [analog_signal(t) for t in timestamps]
plt.plot(timestamps, signal_values)
plt.show()

"""Посмотрим на минимальные и максимальные значения функции"""

np.min(signal_values), np.max(signal_values)

