"""Видим, что график на вид почти не изменился. Это логично: разница между соседними уровнями 5/32, что практически не видно на таком небольшом графике
В целом график выглядит достаточно "шумно" за счет суммы косинусоид с большой частотой.

Фильтр низких частот должен убрать наиболее резкие пики и сделать форму косинусоиды с частотой 1 более заметной.

## Строим фильтр
"""

def kaizer(delta, f_disc, width # полоса перехода
           ): # 3
    A = (-20) * np.log10(delta)
    if A <= 21:
        D = 0.9222
    else:
        D = (A - 7.95)/14.36
    M = (f_disc * D)/width
    return int(np.ceil(M))

class LowFreqFilter():
    def __init__(self, fc, fz, Ap, Az, f_disc=128):
        self.fc = fc
        self.fz = fz
        self.Ap = Ap
        self.Az = Az
        self.f_disc = f_disc
        self._compute_deltas()  # п1 seld.delta1, self.delta2, self.delta
        self._compute_width()  # п2 self.width, self.middle
        self._compute_coefs()

    def _compute_deltas(self):
        self.delta1 = (10**(0.05 * self.Ap) - 1)/(10**(0.05 * self.Ap) + 1)
        self.delta2 = 10**(-0.05 * self.Az)
        self.delta = min(self.delta1, self.delta2)
        
    def _compute_width(self):
        self.width = self.fz - self.fc
        self.middle = self.fc + (self.width * 0.5)

    def _compute_h_simple(self, i, M, vals_array):
        a0 =  2 * self.middle/self.f_disc
        if M % 2 == 0:
            fourier_func = lambda i: a0 if i == 0 else np.sin(np.pi * i * a0)/(np.pi * i)
        else:
            fourier_func = lambda i: a0 if i == 0.5 else np.sin(np.pi * (i - 0.5) * a0)/(np.pi * (i - 0.5))
        
        if i < M/2:
            h = fourier_func(M/2 - i)
        elif i * 2 == M:
            h = a0
        else:
            h = vals_array[M - i]
        return h
    
    def _compute_coefs(self):
        m_initial = kaizer(self.delta, self.f_disc, self.width) + 79 # п3
        print(m_initial)
        M = m_initial

        check = False
        while check ==  False:
            vals_array = []
            for i in range(M + 1):
                hi = self._compute_h_simple(i, M, vals_array) #6 
                vals_array.append(hi)
            check = True # нужен self.check_condions(vals_array)
            print('hooray', M)
            if check == False:
                M += 1
        self.hs = vals_array
    
    def apply_filter(self, values):
        filtered_vals = []
        def vj(values, i, j):
            if i - j > 0:
                return values[i - j]
            else:  # считаем что сигнал в отрицательные моменты времени равен 0
                return 0
        
        for i, value in enumerate(values):
            val = sum([self.hs[j] * vj(values, i, j) for j in range(len(self.hs))])
            filtered_vals.append(val)
        return filtered_vals
    
    def frequency_response(self, omega, hs=None):
        # function of j omega
        if hs is None:
            hs = self.hs
        return sum([hs[i] * np.exp((-1j * i * omega)/self.f_disc) for i in range(len(hs))])

    def amplitude_char(self, omega, hs=None):
        return np.abs(self.frequency_response(omega, hs))

    def compute_realization_chars(self):
        Lo = len(self.hs) - 1  # число элементов задержки -- число всех коэффициентов, кроме 0-го
        Ln = len(set(self.hs))  # число различных коэффициенты
        Vy = len(self.hs)  # число операций умножения = коэффициентов
        Vc = len(self.hs) - 1  # число операция сложения = число коэффициентов - 1
        print('коэффициентов, N:', len(self.hs))
        print('\n')
        print('Реализационные характеристики \n')
        print('ячеек оперативной памяти, Lo:', Lo)
        print('ячеек постоянной памяти, Ln:', Ln)
        print('операций умножения, Vy:', Vy)
        print('операций алгебраического сложения, Vc:', Vc)

lff = LowFreqFilter(fc=15, fz=22, Ap=2, Az=30)

"""Теперь посчитаем реализационные характеристики фильтра"""

lff.compute_realization_chars()

